 SELECT '<Placemark><name>Reguleringsplaner</name><visibility>0</visibility><styleUrl>#transRedPoly</styleUrl><MultiGeometry>' || string_agg(b.kml, '')|| '</MultiGeometry></Placemark>' from (select st_askml( (a.p_geom).geom) as kml, a.id from (SELECT lokalid as id, ST_Dump(geom) AS p_geom from public.app_rphandlingsrom )  AS a ) as b group by b.id;
